class FindtheDifference_Solution2{
    // 2 ms
    // 40.8 MB
    public char findTheDifference(String s, String t){
        char ctNext=0;

        for (int i = 0; i < s.length(); i++) {
            ctNext += (char) (t.charAt(i) - s.charAt(i));
        }

        return (char) (ctNext + t.charAt(t.length()-1));
    }
}